query:
query ConnectionsForStopPlace($id:String!,$start:DateTime!,$range:Int!,$departures:Int!) {
stopPlace(id:$id) {
estimatedCalls(startTime:$start, timeRange:$range, numberOfDepartures:$departures) { ...estimatedCallFields }
}
}

fragment estimatedCallFields on EstimatedCall {
aimedDepartureTime
expectedDepartureTime
realtime
forBoarding
quay { ...quayFields }
serviceJourney { ...serviceJourneyFields }
destinationDisplay { frontText }
situations { ...situationFields }
notices { text }
}

fragment quayFields on Quay {
id
publicCode
description
}

fragment serviceJourneyFields on ServiceJourney {
id
journeyPattern { line { ...lineFields } notices { text } }
notices { text }
}

fragment lineFields on Line {
publicCode
transportMode
transportSubmode
notices { text }
}

fragment situationFields on PtSituationElement {
situationNumber
summary { value }
description { value }
detail { value }
validityPeriod { startTime endTime }
reportType
infoLink
}
