query TripQuery($fromPlaceName: String $to: Location!, $submodeFilter1: TransportSubmodeFilter ) {
  trip(from: {place: "NSR:StopPlace:5199", name:$fromPlaceName, coordinates: {latitude: 3.0, longitude: 1.0}}, to: $to, numTripPatterns: 3, dateTime: "2017-11-19T23:51:14.000+0100", arriveBy: false, transportSubmodes: [$submodeFilter1, { transportMode: rail, transportSubmodes: [interregionalRail]}]) {
    tripPatterns {
      startTime
      duration
      walkDistance
      legs {
        mode
        distance
        line {
          id
          publicCode
        }
        pointsOnLink {
          points
          length
        }
      }
    }
    messageStrings
  }
}
